import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { ApiService, User } from '../services/api.service';

@Component({
  selector: 'app-style-request',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './style-request.component.html',
  styleUrls: ['./style-request.component.css']
})
export class StyleRequestComponent implements OnInit {
  users: User[] = [];
  selectedUserId: number = 1;
  requestText: string = 'I have a wedding in Tuscany next month, help me look sharp';
  loading: boolean = false;
  workflowResponse: any = null;
  error: string = '';

  constructor(private apiService: ApiService) { }

  ngOnInit(): void {
    this.loadUsers();
  }

  loadUsers(): void {
    this.apiService.getAllUsers().subscribe({
      next: (users) => {
        this.users = users;
      },
      error: (err) => {
        this.error = 'Failed to load users: ' + err.message;
        console.error(err);
      }
    });
  }

  curateOutfit(): void {
    if (!this.requestText.trim()) {
      this.error = 'Please enter a style request';
      return;
    }

    this.loading = true;
    this.error = '';
    this.workflowResponse = null;

    this.apiService.curateOutfit(this.selectedUserId, this.requestText).subscribe({
      next: (response) => {
        this.workflowResponse = response;
        this.loading = false;
      },
      error: (err) => {
        this.error = 'Error: ' + (err.error?.error || err.message);
        this.loading = false;
        console.error(err);
      }
    });
  }

  getAgentColor(agentName: string): string {
    const colors: { [key: string]: string } = {
      'The Concierge': '#1976d2',
      'The Historian': '#388e3c',
      'The Trend Analyst': '#f57c00',
      'The Inventory Scout': '#c2185b',
      'The Critic': '#7b1fa2'
    };
    return colors[agentName] || '#999';
  }
}
<div class="container">
  <h1>üé© Style Syndicate - Fashion Consultation</h1>

  <div class="form-section">
    <div class="form-group">
      <label for="user">Select User:</label>
      <select id="user" [(ngModel)]="selectedUserId" [disabled]="loading">
        <option *ngFor="let user of users" [value]="user.id">
          {{ user.firstName }} {{ user.lastName }} (Budget: ${{ user.budget }})
        </option>
      </select>
    </div>

    <div class="form-group">
      <label for="request">Style Request:</label>
      <textarea
        id="request"
        [(ngModel)]="requestText"
        [disabled]="loading"
        placeholder="Describe your style needs..."
        rows="4"
      ></textarea>
    </div>

    <button
      (click)="curateOutfit()"
      [disabled]="loading"
      class="btn-primary"
    >
      {{ loading ? 'Curating Outfit...' : 'Get Style Recommendation' }}
    </button>
  </div>

  <div *ngIf="error" class="error-message">
    ‚ùå {{ error }}
  </div>

  <div *ngIf="loading" class="loading">
    <p>üîÑ Processing your request through our agent network...</p>
  </div>

  <div *ngIf="workflowResponse" class="response-section">
    <h2>Workflow Results</h2>

    <div class="agent-messages">
      <h3>Agent Conversation:</h3>
      <div *ngFor="let msg of workflowResponse.messages" class="agent-message">
        <div class="agent-header" [style.backgroundColor]="getAgentColor(msg.agent)">
          <strong>{{ msg.agent }}</strong> - {{ msg.role }}
        </div>
        <div class="agent-content">
          {{ msg.content }}
        </div>
      </div>
    </div>

    <div *ngIf="workflowResponse.finalOutfit" class="final-outfit">
      <h3>‚ú® Final Curated Outfit</h3>
      <div class="outfit-details">
        <p><strong>Total Price:</strong> ${{ workflowResponse.finalOutfit.totalPrice }}</p>
        <p><strong>Critic Feedback:</strong> {{ workflowResponse.finalOutfit.criticFeedback }}</p>
        
        <h4>Why We Picked These Items:</h4>
        <ul>
          <li *ngFor="let justification of workflowResponse.finalOutfit.justifications">
            {{ justification }}
          </li>
        </ul>
      </div>
    </div>

    <div class="status" [ngClass]="workflowResponse.status.toLowerCase()">
      Status: {{ workflowResponse.status }}
    </div>
  </div>
</div>
